import { Component, OnInit, DoCheck } from '@angular/core';

@Component({
  selector: 'app-give',
  templateUrl: './give.component.html',
  styleUrls: ['./give.component.css']
})
export class GiveComponent implements OnInit, DoCheck {

  constructor() { }

  ngOnInit(): void {
      this.totalgift = this.watercount * 50 + this.bootcount * 425 + this.coatcount * 550 + this.drinkcount * 125 + this.tentcount * 1125 + this.toycount * 450 + this.bookcount * 250 + this.firstaidcount * 850;
  }

  ngDoCheck(): void {
      this.totalgift = this.watercount * 50 + this.bootcount * 425 + this.coatcount * 550 + this.drinkcount * 125 + this.tentcount * 1125 + this.toycount * 450 + this.bookcount * 250 + this.firstaidcount * 850;
  }

  onLoadPaymentData($event) {

  }

  totalgift = 0;
  tendollars = true;
  total = "$50";

  //Item costs in Liras
  water = 50;
  protien_drink = 125;
  tents = 1125;
  coats = 550;
  boots = 425;
  toys = 450;
  books = 250;
  firstaid = 850;
  ;

  //Item counts
  watercount = 1;
  bootcount = 1;
  coatcount = 1;
  drinkcount = 1;
  tentcount = 1;
  toycount = 1;
  bookcount = 1;
  firstaidcount = 1;

